import React from "react";
import { Router, Route, withRouter } from "react-router-dom";
import StudentListItem from "./StudentListItem";
import studentdata from "../utils/studentdata"
import history from './history';

export class StudentList extends React.Component {
  constructor (props) {
    super(props)
    this.state =  {
      students: studentdata,
    };
    
  }
  onChange = (e) => {
    this.props.history.push(`/${e.target.value}`);
  };
  render() {
    const items = this.state.students;
    console.log(items);
    const names = items.map((item) => item.name);
    const uniquenames = [...new Set(names)];
    const listOfStudents = uniquenames
      ? uniquenames.map((uniquename) => (
          <StudentListItem
            value="first-route"
            item={uniquename}
          ></StudentListItem>
        ))
      : "";

    return (
      <div>
        <select onChange={this.onChange}>{listOfStudents}</select>
      </div>
    );
  }
}

const Menu = withRouter(StudentList);

export function StudentRouter() {
  return (
    <BrowserRouter>
      <div className="App">
        <Route
          path="/first-route"
          render={() => <h1>Dit is de pagina van</h1>}
        />
        <Menu />
      </div>
    </BrowserRouter>
  );
}
