    // const data = xyobjectsforfunNumbersFirstAssignment;
     const data = [{x: 0, y: 4}]
    return (
      <div className="Chart">
        <XYPlot height={300} width={300}>
          <HorizontalGridLines />
          <VerticalBarSeries data={data} />
          <XAxis />
          <YAxis />
          <ChartLabel
            text="Assignments"
            className="alt-x-label"
            includeMargin={false}
            xPercent={0.025}
            yPercent={1.01}
          />

          <ChartLabel
            text="Funfactor & difficulty"
            className="alt-y-label"
            includeMargin={false}
            xPercent={0.06}
            yPercent={0.06}
            style={{
              transform: "rotate(-90)",
              textAnchor: "end",
            }}
          />
        </XYPlot>
      </div>
    );
  }
}

export default Chart;
