import React from "react";
import StudentListItem from "./StudentListItem";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

class StudentList extends React.Component {
  render() {
    const { items, handleClick } = this.props;
    const names = items.map((item) => item.name);
    const uniquenames = [...new Set(names)];
    const listOfStudentNames = uniquenames
      ? uniquenames.map((uniquename) => (
          <StudentListItem key={uniquename} clickItem={handleClick} item={uniquename}></StudentListItem>
        ))
      : "";
      const options = <Dropdown options={options} onChange={this._onSelect} value={defaultOption} placeholder="Select an option" />;
      const defaultOption = options[0];
      
    return (
      
      <div>
        <ul>{listOfStudentNames}</ul>
        <Dropdown options={options} onChange={this._onSelect} value={defaultOption} placeholder="Select an option" />;
      </div>
    );
  }
}

export default StudentList;
